package org.marid.ide.extensions

import javafx.beans.binding.*
import javafx.beans.binding.Bindings.*
import javafx.beans.value.*

fun <T, R> ObservableValue<T>.map(func: (T) -> R): ObjectBinding<R> = createObjectBinding(java.util.concurrent.Callable { func(value)}, this)
fun <T> ObservableValue<T>.mapDouble(func: (T) -> Double): DoubleBinding = createDoubleBinding(java.util.concurrent.Callable { func(value) }, this)
fun ObservableDoubleValue.map(func: (Double) -> Double): DoubleBinding = createDoubleBinding(java.util.concurrent.Callable { func(get()) }, this)
fun ObservableDoubleValue.mapFloat(func: (Double) -> Float): FloatBinding = createFloatBinding(java.util.concurrent.Callable { func(get()) }, this)
fun ObservableDoubleValue.mapLong(func: (Double) -> Long): LongBinding = createLongBinding(java.util.concurrent.Callable { func(get()) }, this)
fun ObservableDoubleValue.mapInt(func: (Double) -> Int): IntegerBinding = createIntegerBinding(java.util.concurrent.Callable { func(get()) }, this)
fun ObservableDoubleValue.mapString(func: (Double) -> String): StringBinding = createStringBinding(java.util.concurrent.Callable { func(get()) }, this)
fun ObservableDoubleValue.mapBoolean(func: (Double) -> Boolean): BooleanBinding = createBooleanBinding(java.util.concurrent.Callable { func(get()) }, this)
